def longest_palindrome(s):
    def check_through_center(left, right):
        while left >= 0 and right < len(s) and s[left] == s[right]:
            left -= 1
            right += 1
        return s[left + 1:right]

    longest_substring = ""
    
    for i in range(len(s)):
        odd = check_through_center(i, i)
        if len(odd) > len(longest_substring):
            longest_substring = odd
        even = check_through_center(i, i + 1)
        if len(even) > len(longest_substring):
            longest_substring = even
    return longest_substring

